The general flow of the document continues.changeDisplayState = function (id) {var d = document.getElementById('showhide'),e = document.getElementById(id);if (e.style.display === 'none' || e.style.display === '') {e.style.display = 'block';d.innerHTML = 'Hide paragraph';} else {e.style.display = 'none';d.innerHTML = 'Show paragraph';}};document.getElementById('showhide').onclick = function () {changeDisplayState('toggleMe');return false;};
